transactions.ClaimGem:
import NonFungibleToken from 0xNonFungibleToken
import MetadataViews from 0xMetadataViews
import Gem from 0xGem
import GemGames from 0xGemGames

pub fun initializeCollection(account: AuthAccount) {
    if account.borrow<&Gem.Collection>(from: Gem.CollectionStoragePath) == nil {
        let collection <- Gem.createEmptyCollection()

        account.save(<-collection, to: Gem.CollectionStoragePath)

        account.link<&Gem.Collection{NonFungibleToken.CollectionPublic, Gem.GemCollectionPublic, MetadataViews.ResolverCollection}>(
            Gem.CollectionPublicPath, 
            target: Gem.CollectionStoragePath
        )
    }

}

transaction(claimAddress: Address, setId: UInt64) {
    let claimerAddress: Address

    prepare(signer: AuthAccount) {
        self.claimerAddress = signer.address
        initializeCollection(account: signer)
    }

    execute {
        let gameRef = getAccount(claimAddress).getCapability(GemGames.GemGameManagerPublicPath)!.borrow<&GemGames.GemGameManager{GemGames.IGameManagerPublic}>()!.borrowGame(setId: setId)!

        gameRef.claim(address: self.claimerAddress)
    }
}

transactions.CreateGemGame:
import NonFungibleToken from 0xNonFungibleToken
import MetadataViews from 0xMetadataViews
import Gem from 0xGem
import GemGames from 0xGemGames

pub fun getOrCreateGameCollection(
    account: AuthAccount,
    setId: UInt64
): Capability<&Gem.Collection> {
     let collectionCap = account.getCapability<&Gem.Collection>(GemGames.getGameCollectionPrivatePath(setId: setId))

     if collectionCap.check() {
        return collectionCap
     }

     let collection <- Gem.createEmptyCollection()
     account.save(<-collection, to: GemGames.getGameCollectionStoragePath(setId: setId))
     account.link<&Gem.Collection>(GemGames.getGameCollectionPrivatePath(setId: setId), target: GemGames.getGameCollectionStoragePath(setId: setId))
     account.link<&Gem.Collection{NonFungibleToken.CollectionPublic, Gem.GemCollectionPublic, MetadataViews.ResolverCollection}>(GemGames.getGameCollectionPublicPath(setId: setId), target: GemGames.getGameCollectionStoragePath(setId: setId))

     return collectionCap

}

transaction(name: String, prizes: String) {
    let gemGameManagerRef: &GemGames.GemGameManager

    prepare(signer: AuthAccount) {
        self.gemGameManagerRef = signer.borrow<&GemGames.GemGameManager>(from: GemGames.GemGameManagerStoragePath)
            ?? panic("Could not borrow reference to the GemGameManager")

        let setId = self.gemGameManagerRef.createSet()

        self.gemGameManagerRef.createGame(setId: setId, name: name, prizes: prizes, setCollection: getOrCreateGameCollection(account: signer, setId: setId))
    }

    execute {}
}

transactions.CreateSecretMessageVault:
import VaultService from 0xVaultService

transaction(description: String, thumbnail: String, passwordSalt: String, hashControl: String, hashAlgorithm: String, encryptedMessage: String?, encryptionAlgorithm: String?, derivedPublicKey: String?) {
  let vaultCollection: &VaultService.VaultCollection

  prepare(signer: AuthAccount) {
    var vaultCollectionCap = signer.borrow<&VaultService.VaultCollection>(from: /storage/VaultCollection)
    if (vaultCollectionCap == nil) {
      let vaultCollection <- VaultService.createVaultCollection()
      signer.save(<-vaultCollection, to: /storage/VaultCollection)
      vaultCollectionCap = signer.borrow<&VaultService.VaultCollection>(from: /storage/VaultCollection)
      self.vaultCollection = vaultCollectionCap!
    } else {
      self.vaultCollection = vaultCollectionCap!
    }
    signer.link<&{VaultService.VaultCollectionPublic}>(/public/VaultCollection, target: /storage/VaultCollection)
  }

  execute {
    let vault <- VaultService.createVault(description: description, thumbnail: thumbnail, passwordSalt: passwordSalt, hashControl: hashControl, hashAlgorithm: hashAlgorithm, encryptedMessage: encryptedMessage, encryptionAlgorithm: encryptionAlgorithm, derivedPublicKey: derivedPublicKey, action: nil)
    self.vaultCollection.deposit(vault: <-vault)
  }
}

transactions.MintGems:
import GemGames from 0xGemGames


transaction(setId: UInt64, colors: [String]) {
    let gemGameManagerRef: &GemGames.GemGameManager

    prepare(signer: AuthAccount) {
        self.gemGameManagerRef = signer.borrow<&GemGames.GemGameManager>(from: GemGames.GemGameManagerStoragePath)
            ?? panic("Could not borrow reference to the GemGameManager")

    }


    execute {
        for color in colors {
            self.gemGameManagerRef.mintNFT(setId: setId, color: color)
        }

    }
}

transactions.SetupGemGameManager:
import GemGames from 0xGemGames

transaction() {
    prepare(signer: AuthAccount) {
        if signer.borrow<&GemGames.GemGameManager>(from: GemGames.GemGameManagerStoragePath) != nil {
            return
        }
        signer.save(<- GemGames.createGemGameManager(), to: GemGames.GemGameManagerStoragePath)
        signer.link<&GemGames.GemGameManager>(GemGames.GemGameManagerPrivatePath, target: GemGames.GemGameManagerStoragePath)
        signer.link<&GemGames.GemGameManager{GemGames.IGameManagerPublic}>(GemGames.GemGameManagerPublicPath, target: GemGames.GemGameManagerStoragePath)
    }
}

